GlobalInformation:
query GlobalInformation {
  globalInfo {
    languages {
      id
      backendID
      language
      code
      __typename
    }
    __typename
  }
}

LivestreamPage:
query LivestreamPage($displayname: String!, $add: Boolean!, $isLoggedIn: Boolean!) {
  userByDisplayName(displayname: $displayname) {
    id
    ...VDliveAvatarFrag
    ...VDliveNameFrag
    ...VFollowFrag
    ...VSubscriptionFrag
    banStatus
    about
    avatar
    myRoomRole @include(if: $isLoggedIn)
    isMe @include(if: $isLoggedIn)
    isSubscribing @include(if: $isLoggedIn)
    livestream {
      id
      permlink
      watchTime(add: $add)
      ...LivestreamInfoFrag
      ...VVideoPlayerFrag
      __typename
    }
    hostingLivestream {
      id
      creator {
        ...VDliveAvatarFrag
        displayname
        username
        __typename
      }
      ...VVideoPlayerFrag
      __typename
    }
    ...LivestreamProfileFrag
    __typename
  }
}

fragment LivestreamInfoFrag on Livestream {
  category {
    title
    imgUrl
    id
    backendID
    __typename
  }
  title
  watchingCount
  totalReward
  ...VDonationGiftFrag
  ...VPostInfoShareFrag
  __typename
}

fragment VDonationGiftFrag on Post {
  permlink
  creator {
    username
    __typename
  }
  __typename
}

fragment VPostInfoShareFrag on Post {
  permlink
  title
  content
  category {
    id
    backendID
    title
    __typename
  }
  __typename
}

fragment VDliveAvatarFrag on User {
  avatar
  __typename
}

fragment VDliveNameFrag on User {
  displayname
  partnerStatus
  __typename
}

fragment LivestreamProfileFrag on User {
  isMe @include(if: $isLoggedIn)
  canSubscribe
  private @include(if: $isLoggedIn) {
    subscribers {
      totalCount
      __typename
    }
    __typename
  }
  videos {
    totalCount
    __typename
  }
  pastBroadcasts {
    totalCount
    __typename
  }
  followers {
    totalCount
    __typename
  }
  following {
    totalCount
    __typename
  }
  ...ProfileAboutFrag
  __typename
}

fragment ProfileAboutFrag on User {
  id
  about
  __typename
}

fragment VVideoPlayerFrag on Livestream {
  disableAlert
  category {
    id
    title
    __typename
  }
  language {
    language
    __typename
  }
  __typename
}

fragment VFollowFrag on User {
  id
  username
  displayname
  isFollowing @include(if: $isLoggedIn)
  isMe @include(if: $isLoggedIn)
  followers {
    totalCount
    __typename
  }
  __typename
}

fragment VSubscriptionFrag on User {
  id
  username
  displayname
  isSubscribing @include(if: $isLoggedIn)
  canSubscribe
  isMe @include(if: $isLoggedIn)
  __typename
}

LivestreamProfileFollowers:
query LivestreamProfileFollowers($displayname: String!, $sortedBy: RelationSortOrder, $first: Int, $after: String, $isLoggedIn: Boolean!) {
 userByDisplayName(displayname: $displayname) {
 id
 displayname
 followers(sortedBy: $sortedBy, first: $first, after: $after) {
 pageInfo {
 endCursor
 hasNextPage
 __typename
 }
 list {
 ...VDliveAvatarFrag
 ...VDliveNameFrag
 ...VFollowFrag
 __typename
 }
 __typename
 }
 __typename
 }
}

fragment VDliveAvatarFrag on User {
 avatar
 __typename
}

fragment VDliveNameFrag on User {
 displayname
 partnerStatus
 __typename
}

fragment VFollowFrag on User {
 id
 username
 displayname
 isFollowing @include(if: $isLoggedIn)
 isMe @include(if: $isLoggedIn)
 followers {
 totalCount
 __typename
 }
 __typename
}

StreamMessageSubscription:
subscription StreamMessageSubscription($streamer: String!) {
  streamMessageReceived(streamer: $streamer) {
    type
    ... on ChatGift {
      id
      gift
      amount
      recentCount
      expireDuration
      ...VStreamChatSenderInfoFrag
    }
    ... on ChatHost {
      id
      viewer
      ...VStreamChatSenderInfoFrag
    }
    ... on ChatSubscription {
      id
      month
      ...VStreamChatSenderInfoFrag
    }
    ... on ChatChangeMode {
      mode
    }
    ... on ChatText {
      id
      content
      ...VStreamChatSenderInfoFrag
    }
    ... on ChatFollow {
      id
      ...VStreamChatSenderInfoFrag
    }
    ... on ChatDelete {
      ids
    }
    ... on ChatBan {
      id
      ...VStreamChatSenderInfoFrag
    }
    ... on ChatModerator {
      id
      ...VStreamChatSenderInfoFrag
      add
    }
    ... on ChatEmoteAdd {
      id
      ...VStreamChatSenderInfoFrag
      emote
    }
  }
}

fragment VStreamChatSenderInfoFrag on SenderInfo {
  subscribing
  role
  roomRole
  sender {
    id
    username
    displayname
    avatar
    partnerStatus
  }
}

TopContributors:
query TopContributors($displayname: String!, $rule: ContributionSummaryRule, $first: Int, $after: String, $queryStream: Boolean!) {
  userByDisplayName(displayname: $displayname) {
    id
    ...TopContributorsOfStreamerFrag @skip(if: $queryStream)
    livestream @include(if: $queryStream) {
      ...TopContributorsOfLivestreamFrag
      __typename
    }
    __typename
  }
}

fragment TopContributorsOfStreamerFrag on User {
  id
  topContributions(rule: $rule, first: $first, after: $after) {
    pageInfo {
      endCursor
      hasNextPage
      __typename
    }
    list {
      amount
      contributor {
        id
        ...VDliveNameFrag
        ...VDliveAvatarFrag
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}

fragment VDliveNameFrag on User {
  displayname
  partnerStatus
  __typename
}

fragment VDliveAvatarFrag on User {
  avatar
  __typename
}

fragment TopContributorsOfLivestreamFrag on Livestream {
  id
  topContributions(first: $first, after: $after) {
    pageInfo {
      endCursor
      hasNextPage
      __typename
    }
    list {
      amount
      contributor {
        id
        ...VDliveNameFrag
        ...VDliveAvatarFrag
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}
